mapper.update:
update sau_student_course_msg set state = 0 where course_id in ( select course_id from sau_course_info_msg where teacher_id in
#{teacherId}
)

mapper.select:
select teacher_id as "teacherId", teacher_name as "teacherName", case teacher_sex when 0 then '男' when 1 then '女' when 2 then '未知' End as "teacherSex", teacher_birthday as "teacherBirthday", department_id as "departmentId", teacher_title as "teacherTitle", teacher_technical as "teacherTechnical" from sau_teacher_info_msg
state = 1
and teacher_id = #{teacherId}
and teacher_name like concat('%',#{teacherName},'%')
and department_id = #{departmentId}
